mapper.update:
update moong_table
set title= #{title}, content = #{content}
where bno = #{bno}

mapper.select:
/** BoardMapper - selectFileInfo */
SELECT
    STORED_FILE_NAME,
    ORG_FILE_NAME
FROM MP_FILE
WHERE FILE_NO = #{FILE_NO}

mapper.delete:
/** BoardMapper - removeFile */

DELETE FROM MP_FILE
WHERE FILE_NO = #{FILE_NO}

mapper.update:
UPDATE MP_FILE SET
    DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}
