mapper.insert:
insert into member_table
(
    memberEmail                /* 회원 이메일 */
,memberPassword             /* 회원 비밀번호 */
,memberName                 /* 회원 이름 */
,memberAge                  /* 회원 나이 */
,memberMobile               /* 회원 전화번호*/
)
values
    (
        #{memberEmail}
    ,#{memberPassword}
    ,#{memberName}
    ,#{memberAge}
    ,#{memberMobile}
    )
