mapper.insert:
/*********************** 회원 가입 AnimaMemberMapper.xml -  signup **************************/

        INSERT INTO OWNER_TBL
        (
        OWNER_ID,           /** 회원 아이디 **/
        PASSWORD,            /** 회원 비밀번호 **/
        EMAIL,               /** 회원 이메일 **/
        PHONENUMBER         /** 회원 핸드폰번호 **/
        )
        VALUES
        (
        #{owner_Id},
        #{password},
        #{email},
        #{phoneNumber}
        )

/*********************** 회원 가입 AnimaMemberMapper.xml -  signup **************************/

mapper.select:
/*********************** 로그인 AnimaMemberMapper.xml - login **************************/
SELECT OWNER_ID, /** 회원 아이디 **/ PASSWORD, /** 회원 비밀번호 **/ EMAIL, /** 회원 이메일 **/ PHONENUMBER, /** 회원 핸드폰번호 **/ GRADE, /** 등급 **/ PWDEXPIRES, /** 비밀번호 변경 날짜 **/ PWFAILCOUNT, /** 비밀번호 실패 횟수 **/ CASE WHEN PWDEXPIRES <= DATE_ADD(NOW(), INTERVAL -3 MONTH) THEN 'Y' ELSE 'N' END AS CHANGE_AT FROM OWNER_TBL WHERE OWNER_ID = #{owner_Id}
/*********************** 로그인 AnimaMemberMapper.xml - login **************************/

/*********************** 이메일 체크 AnimaMemberMapper.xml -  animal.emailCheck **************************/

        SELECT
            COUNT(*)
        FROM
            OWNER_TBL
        WHERE
            1 = 1

            AND  EMAIL =  #{email}


            AND  OWNER_ID =  #{ownerId}


/*********************** 이메일 체크 AnimaMemberMapper.xml -  emailCheck **************************/

/*********************** 내 정보 AnimaMemberMapper.xml - myInfo **************************/
SELECT ( SELECT COUNT(*) FROM MEDICAL_REVIEW_BAORD /** 리뷰 몇개 썼는지 개수 찾는 쿼리 **/ WHERE WRITER = #{owner_Id} ) AS POST,
( SELECT COUNT(*) FROM INQUIRY_COMMENT_TBL /** 댓글 몇개 썼는지 개수 찾는 쿼리 **/ WHERE COMMENT_WRITER = #{owner_Id}) AS COMMENT,
OWNER_ID, /** 회원 아이디 **/ EMAIL, /** 회원 이메일 **/ PHONENUMBER /** 회원 핸드폰번호 **/ FROM OWNER_TBL WHERE OWNER_Id = #{owner_Id}
/*********************** 내 정보 AnimaMemberMapper.xml - myInfo **************************/

mapper.update:
/*********************** 내 정보 수정 AnimaMemberMapper.xml.xml -  editInfo **************************/

        UPDATE
            OWNER_TBL
        SET
            EMAIL = #{email}, PHONENUMBER =  #{phoneNumber}
        WHERE
            OWNER_ID = #{owner_Id}

/*********************** 내 정보 수정 AnimaMemberMapper.xml -  editInfo **************************/

/*********************** 자동로그인 AnimaMemberMapper.xml.xml -  keepLogin **************************/

        UPDATE
            OWNER_TBL
        SET
            SESSION_KEY = #{session_key},
            session_limit = #{session_limit}
        WHERE
            OWNER_ID = #{owner_Id}

/*********************** 자동로그인 AnimaMemberMapper.xml.xml -  keepLogin **************************/

mapper.select:
/*********************** 자동로그인 AnimaMemberMapper.xml.xml - keepLogin **************************/
SELECT OWNER_ID, /** 회원 아이디 **/ PASSWORD, /** 회원 비밀번호 **/ EMAIL, /** 회원 이메일 **/ PHONENUMBER, /** 회원 핸드폰번호 **/ GRADE, /** 등급 **/ PWDEXPIRES, /** 비번 만료일 **/ SESSION_KEY, /** 세션 키 **/ CASE WHEN PWDEXPIRES <= DATE_ADD(NOW(), INTERVAL -3 MONTH) THEN 'Y' ELSE 'N' END AS CHANGE_AT FROM OWNER_TBL WHERE SESSION_KEY = #{value}
/*********************** 자동로그인 AnimaMemberMapper.xml.xml - keepLogin **************************/

/*********************** 아이디 찾기 AnimaMemberMapper.xml.xml -  findId **************************/

        SELECT
            OWNER_ID
        FROM
            OWNER_TBL
        WHERE
            EMAIL = #{email} AND PHONENUMBER = #{phone}

/*********************** 아이디 찾기 AnimaMemberMapper.xml.xml -  findId **************************/

/*********************** 비번 찾기 AnimaMemberMapper.xml.xml -  findPw **************************/

        SELECT
            COUNT(*)
        FROM
            OWNER_TBL
        WHERE
            EMAIL = #{email} AND OWNER_ID = #{owner_Id}

/*********************** 비번 찾기 AnimaMemberMapper.xml.xml -  findPw **************************/

mapper.update:
/*********************** 랜덤으로 비번저장  AnimaMemberMapper.xml.xml -  pwUpdate **************************/

        UPDATE
            OWNER_TBL
        SET
            PASSWORD = #{password}
        WHERE
            EMAIL = #{email} AND OWNER_ID = #{owner_Id}

/*********************** 랜덤으로 비번저장  AnimaMemberMapper.xml.xml -  pwUpdate **************************/

/*********************** 비밀번호 변경  AnimaMemberMapper.xml.xml -  pwUpdate **************************/

        UPDATE
            OWNER_TBL
        SET

            PASSWORD = #{password},

            PWDEXPIRES = CURRENT_TIMESTAMP
        WHERE
            OWNER_ID = #{owner_Id}

/*********************** 랜덤으로 비번저장  AnimaMemberMapper.xml.xml -  pwUpdate **************************/
