mapper.select:
/************************ 문의 게시판 상세보기 inquiry.read - inquiry.read *****************************/
SELECT TITLE, /** 문의제목 **/ CONTENT, /** 문의내용 **/ WRITER, /** 작성자 **/ OWNER_ID, /** 소유자 ID **/ PASSWORD, /** 비밀번호 **/ REGDATE, UPDATE_DATE, INQUIRY_NUM, CONCAT ( LEFT(WRITER, 4), REPEAT('*', LENGTH(WRITER) - 9), RIGHT(WRITER, 4) ) AS HIDDENWRITER FROM INQUIRY_TBL WHERE INQUIRY_NUM = #{inquiry_num} /** 문의고유번호 **/
AND PASSWORD = #{pw}

mapper.delete:
/*********************** 문의 게시판 게시물 삭제 InquiryMapper.xml - inquiry.delete *************************/

        DELETE
        FROM
            INQUIRY_TBL
       WHERE
           INQUIRY_NUM=#{inquiry_Num}
