mapper.select:
/************************ 문의 게시판 상세보기 inquiry.read - inquiry.read *****************************/
SELECT TITLE, /** 문의제목 **/ CONTENT, /** 문의내용 **/ WRITER, /** 작성자 **/ OWNER_ID, /** 소유자 ID **/ PASSWORD, /** 비밀번호 **/ REGDATE, UPDATE_DATE, INQUIRY_NUM, OWNER_ID, CASE WHEN CHAR_LENGTH(WRITER) = 3 /** writer 글자 수가 3개인지 확인 **/ THEN CONCAT( LEFT(WRITER, 1), '*', RIGHT(WRITER, 1) ) /** 3개라면 가운대 글자 * 처리 **/ ELSE WRITER /** 아니라면 그냥 전체 보여주기 **/ END AS HIDDENWRITER /** 알리아스 **/ FROM INQUIRY_TBL WHERE INQUIRY_NUM = #{inquiry_num} /** 문의고유번호 **/
AND PASSWORD = #{pw}

mapper.delete:
/*********************** 문의 게시판 게시물 삭제 InquiryMapper.xml - inquiry.delete *************************/

        DELETE
        FROM
            INQUIRY_TBL
       WHERE
           INQUIRY_NUM=#{inquiry_Num}
