mapper.insert:
/**************************** 동물 등록 PetMapper.xml - infoSave *********************************/
INSERT INTO Pet_Tbl ( OWNER_ID, /* 동물 주인 */ PET_ID, /* 마이크로칩 번호 */ PET_NAME, /* 동물 이름 */ AGE, /* 동물 나이 */ SIGNIFICANT, /* 특이사항 */ SPECIES /* 종 */ ) VALUES ( #{owner_Id}, /* 동물 주인 */ #{pet_Id}, /* 마이크로칩 번호 */ #{pet_name}, /* 동물 이름 */ #{age}, /* 동물 나이 */ #{significant}, /* 특이사항 */ #{species} /* 종 */ )
/**************************** 동물 등록 PetMapper.xml - infoSave *********************************/

mapper.select:
/**************************** 동물 정보 PetMapper.xml - PetInfo *********************************/
SELECT OWNER_ID, /* 동물 주인 */ PET_ID, /* 마이크로칩 번호 */ PET_NAME, /* 동물 이름 */ SPECIES, /* 종 */ SIGNIFICANT, /* 특이사항 */ TIMESTAMPDIFF(YEAR, AGE, CURDATE()) AS AGE /* 계산된 나이 */ FROM Pet_Tbl WHERE OWNER_Id = #{owner_Id} /* 주인 아이디 */
/**************************** 동물 정보 PetMapper.xml - PetInfo *********************************/

/**************************** 동물 정보 상세보기 PetMapper.xml - InfoSelect *********************************/
SELECT OWNER_ID, /* 동물 주인 */ PET_ID, /* 마이크로칩 번호 */ PET_NAME, /* 동물 이름 */ AGE, /* 동물 나이 */ SIGNIFICANT, /* 특이사항 */ SPECIES /* 종 */ FROM PET_TBL WHERE PET_ID = #{pet_Id}
/**************************** 동물 정보 상세보기 PetMapper.xml - InfoSelect *********************************/

mapper.update:
/**************************** 동물 정보 상세보기 PetMapper.xml - InfoUpdate *********************************/

        UPDATE
            PET_TBL
        SET
            AGE = #{age},                   /* 동물 나이 */
            SPECIES = #{species},           /* 종 */
            PET_NAME = #{pet_name},         /* 동물 이름 */
            SIGNIFICANT = #{significant}    /* 특이사항 */

        WHERE PET_ID = #{pet_Id};

/**************************** 동물 정보 상세보기 PetMapper.xml - InfoUpdate *********************************/
